export default function Contributions() {
  const router = useRouter();
  const { magic } = useMagicContext();

  const [account, setAccount] = useState<string>("");
  const [campaigns, setCampaigns] = useState<any>([]);

  useEffect(() => {
    const user: any = localStorage.getItem("user");
    setAccount(user);

    const getCreatorCampaignsHandler = async () => {
      const provider = await magic?.wallet.getProvider();
      const web3Provider = new ethers.providers.Web3Provider(provider);
      const res = await getMyCampaigns(web3Provider, account);
      setCampaigns(res);
    };
    getCreatorCampaignsHandler();
  }, [magic, account]);

  return (
    <div className="bg-auth-bg">
      <Header />
      <div className="py-14 px-16 w-full">
        <h2 className="text-white font-bold text-3xl">My Campaigns</h2>
        {campaigns && campaigns.length <= 0 ? (
          <div className=" w-full py-14 flex flex-col justify-center items-center">
            {campaignEmptyIcon}
            <p className="my-8">
              You do not have any campaigns at this time, click the button below
              to create a campaign.
            </p>
            <div className="w-1/4">
              <Button className="text-black w-1/4 flex text-base whitespace-nowrap">
                <span className="mr-3">{plusIcon}</span> Create Campaign
              </Button>
            </div>
          </div>
        ) : (
          <div className="my-10 grid grid-flow-row grid-cols-3 gap-6 w-full">
            {campaigns &&
              campaigns.map((item: any, index: number) => (
                <Card
                  key={index}
                  image={item.image}
                  category={IDToCategory(item.category)}
                  daysLeft={4}
                  title={item.title}
                  subtitle={item.description}
                  creatorPicture={SolarPanel}
                  creator={"Divine Samuel"}
                  raised={item.totalFunds}
                  supporters={item.supporters}
                  goal={item.goal}
                />
              ))}
          </div>
        )}
      </div>
      <div className="py-14 px-16">
        <h2 className="text-white font-bold text-3xl">My Contributions</h2>
        {campaigns && campaigns.length <= 0 ? (
          <div className=" w-full py-14 flex flex-col justify-center items-center">
            {campaignEmptyIcon}
            <p className="my-8">
              You have not made any contributions, click the button below to see
              available campaigns.
            </p>
            <div className="w-1/4">
              <Button
                onClick={() => router.push("/")}
                className="text-black flex text-base whitespace-nowrap"
              >
                <span className="mr-3">{plusIcon}</span> Explore Campaigns
              </Button>
            </div>
          </div>
        ) : (
          <div className="my-10 grid grid-flow-row grid-cols-3 gap-6">
            {campaigns &&
              campaigns.map((item: any, index: number) => (
                <Card
                  key={index}
                  image={SolarPanel}
                  category={IDToCategory(item.category)}
                  daysLeft={4}
                  title={item.title}
                  subtitle={item.description}
                  creatorPicture={SolarPanel}
                  creator={"Divine Samuel"}
                  raised={item.totalFunds}
                  supporters={item.supporters}
                  goal={item.goal}
                />
              ))}
          </div>
        )}
      </div>
    </div>
  );
}
